sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new MicrosoftDynamicsCRMUtils().trimStringInputs(inputs);
	var reqJson = {};
	reqJson["name"] = inputs.name ? inputs.name : undefined;
	reqJson["parentaccountid@odata.bind"] = "/accounts(" + inputs
		.parent_account + ")";
	reqJson["transactioncurrencyid@odata.bind"] =
		"/transactioncurrencies(" + inputs.currency + ")";
	reqJson["pricelevelid@odata.bind"] = "/pricelevels(" + inputs
		.price_list + ")";
	if (inputs.additional_fields) {
		try {
			var FieldsArr = inputs.additional_fields.trim().split(/\^/);
			for (var i = 0; i < FieldsArr.length; i++) {
				var nameValuePair = FieldsArr[i].split(/=(.+)/);
				var keyValueJson = new MicrosoftDynamicsCRMUtils().getValue(
					nameValuePair[0], nameValuePair[1]);
				reqJson[keyValueJson.key] = keyValueJson.value;
			}
		} catch (e) {}
	}
	outputs.reqbody = JSON.stringify(reqJson);

})(inputs, outputs);

