sys_variable_value.value:
(function execute(inputs, outputs) {
	if (inputs.status_code == "201") {
		outputs.status = "Success";
		var response = JSON.parse(inputs.response);
		outputs.opportunity_id = response.opportunityid;
	} else {
		outputs.status = "Error";
		outputs = new MicrosoftDynamicsCRMErrorHandler()
			.createOpportunityErrorHandler(inputs, outputs);
	}
})(inputs, outputs);

