sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new MicrosoftDynamicsCRMUtils().trimStringInputs(inputs);
	outputs.opportunity_id = new MicrosoftDynamicsCRMUtils()
		.validateOpportunity(inputs.opportunity_id);
})(inputs, outputs);

