sys_variable_value.value:
(function execute(inputs, outputs) {
	inputs = new MicrosoftDynamicsCRMUtils().trimStringInputs(inputs);
	if (inputs.status_code == "200") {
		outputs.status = "Success";
		outputs.opportunity_details = inputs.response;
	} else
		outputs = new MicrosoftDynamicsCRMErrorHandler()
		.getOpportunityDetailsErrorHandler(inputs, outputs);
})(inputs, outputs);

